mapper.select:
select count(*)
from member
where userId=#{id}

select userId, userName, userPwd, emailId, emailDomain, joinDate
from member
where userId=#{id}

mapper.update:
update member set
userPwd = #{newPw2}
where userId = #{curId}

update member set 
emailDomain=#{newEmailDomain},
emailId = #{newEmailId}
where userId = #{userId}

mapper.select:
select userId, userName, token
from member
where userId = #{userId} and userPwd = #{userPwd}

select userName, userId, emailId, emailDomain, joinDate
from member
where userId = #{userId}

mapper.update:
update member
set token = #{token}
where userId = #{userId}

mapper.select:
select token
from member
where userId = #{userId}

mapper.update:
update member
set token = #{token, jdbcType=VARCHAR}
where userId = #{userId}
